import gql from 'graphql-tag';

export const GovernQueueEntityQuery = gql`
  query governQueue($ID: String) {
    governQueue(id: $ID) {
      nonce
      config {
        resolver
        executionDelay
        scheduleDeposit {
          amount
          token
          decimals
          name
          symbol
        }
        challengeDeposit {
          amount
          token
          decimals
          name
          symbol
        }
        maxCalldataSize
      }
    }
  }
`;

export const GovernQueueEntityContainersQuery = gql`
  query governQueue($ID: String) {
    governQueue(id: $ID) {
      id
      containers {
        id
        state
        payload {
          id
          proof
          submitter
          executionTime
          actions {
            to
            value
            data
          }
        }
      }
    }
  }
`;

